{"content": "* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #1e3c72 0%, #2a5298 100%);
    color: white;
    overflow: hidden;
}

#app {
    height: 100vh;
    display: flex;
    flex-direction: column;
}

/* Header */
header {
    background: rgba(0, 0, 0, 0.8);
    padding: 1rem 2rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 2px solid #00d4ff;
}

header h1 {
    font-size: 1.8rem;
    font-weight: 600;
}

.status {
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    font-size: 0.9rem;
}

#connection-status {
    color: #00ff88;
    font-weight: 500;
}

#last-update {
    color: #ccc;
    font-size: 0.8rem;
}

/* Main content */
.main-content {
    flex: 1;
    display: flex;
    height: calc(100vh - 120px);
}

/* Globe container */
#globe-container {
    flex: 1;
    position: relative;
    background: #000;
}

#globe {
    width: 100%;
    height: 100%;
    display: block;
}

/* Control panel */
#control-panel {
    width: 320px;
    background: rgba(0, 0, 0, 0.9);
    padding: 1.5rem;
    overflow-y: auto;
    border-left: 2px solid #00d4ff;
}

.panel-section {
    margin-bottom: 2rem;
}

.panel-section h3 {
    color: #00d4ff;
    margin-bottom: 1rem;
    font-size: 1.1rem;
    border-bottom: 1px solid #333;
    padding-bottom: 0.5rem;
}

/* Airport list */
#airport-list {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
}

.airport-item {
    background: rgba(255, 255, 255, 0.1);
    padding: 0.8rem;
    border-radius: 6px;
    cursor: pointer;
    transition: all 0.3s ease;
    border-left: 4px solid transparent;
}

.airport-item:hover {
    background: rgba(255, 255, 255, 0.2);
    transform: translateX(5px);
}

.airport-item.active {
    border-left-color: #00d4ff;
    background: rgba(0, 212, 255, 0.2);
}

.airport-code {
    font-weight: bold;
    font-size: 1.1rem;
}

.airport-name {
    font-size: 0.9rem;
    color: #ccc;
    margin: 0.2rem 0;
}

.turbulence-indicator {
    display: inline-block;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    margin-right: 0.5rem;
}

/* Legend */
.legend {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
}

.legend-item {
    display: flex;
    align-items: center;
    gap: 0.8rem;
}

.legend-color {
    width: 20px;
    height: 20px;
    border-radius: 4px;
    border: 1px solid rgba(255, 255, 255, 0.3);
}

/* Buttons */
.btn {
    background: linear-gradient(135deg, #00d4ff, #0099cc);
    color: white;
    border: none;
    padding: 0.8rem 1rem;
    border-radius: 6px;
    cursor: pointer;
    font-weight: 500;
    margin-bottom: 0.5rem;
    width: 100%;
    transition: all 0.3s ease;
}

.btn:hover {
    background: linear-gradient(135deg, #0099cc, #007aa3);
    transform: translateY(-2px);
}

.btn:active {
    transform: translateY(0);
}

.btn[data-active="false"] {
    background: #666;
}

/* Airport details */
#airport-details {
    background: rgba(255, 255, 255, 0.05);
    padding: 1rem;
    border-radius: 6px;
    border: 1px solid rgba(255, 255, 255, 0.1);
}

.detail-row {
    display: flex;
    justify-content: space-between;
    margin-bottom: 0.5rem;
    padding: 0.3rem 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.detail-label {
    color: #00d4ff;
    font-weight: 500;
}

.detail-value {
    color: white;
}

/* Footer */
footer {
    background: rgba(0, 0, 0, 0.8);
    padding: 0.8rem 2rem;
    text-align: center;
    font-size: 0.9rem;
    color: #ccc;
    border-top: 1px solid #333;
}

/* Loading animation */
.loading {
    display: inline-block;
    width: 12px;
    height: 12px;
    border: 2px solid rgba(255, 255, 255, 0.3);
    border-top: 2px solid #00d4ff;
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

/* Responsive design */
@media (max-width: 1024px) {
    #control-panel {
        width: 280px;
    }

    header h1 {
        font-size: 1.5rem;
    }
}

@media (max-width: 768px) {
    .main-content {
        flex-direction: column;
    }

    #control-panel {
        width: 100%;
        height: 300px;
    }

    #globe-container {
        height: calc(100vh - 420px);
    }
