initializeGlobe() {
    // Add background layers for enhanced visual appeal
    this.wwd.addLayer(new WorldWind.StarFieldLayer());
    this.wwd.addLayer(new WorldWind.AtmosphereLayer());
    
    // Add basic Earth layers
    this.wwd.addLayer(new WorldWind.BMNGOneImageLayer());
    this.wwd.addLayer(new WorldWind.BMNGLandsatLayer());
    this.wwd.addLayer(new WorldWind.BingAerialWithLabelsLayer(null));
    
    // Add day/night terminator layer
    this.dayNightLayer = new WorldWind.RenderableLayer("Day/Night");
    this.wwd.addLayer(this.dayNightLayer);
    this.updateDayNightTerminator();
    
    // Add UI layers
    this.wwd.addLayer(new WorldWind.CompassLayer());
    this.wwd.addLayer(new WorldWind.CoordinatesDisplayLayer(this.wwd));
    this.wwd.addLayer(new WorldWind.ViewControlsLayer(this.wwd));

        // Add click handler
        this.setupClickHandler();
        
        // Start day/night animation
        this.startDayNightAnimation();
    }

    updateDayNightTerminator() {
        if (!this.dayNightLayer) return;
        
        // Clear existing day/night renderables
        this.dayNightLayer.removeAllRenderables();
        
        // Calculate sun position based on current time
        const now = new Date();
        const dayOfYear = Math.floor((now - new Date(now.getFullYear(), 0, 0)) / 86400000);
        const sunDeclination = 23.45 * Math.sin(2 * Math.PI * (284 + dayOfYear) / 365) * Math.PI / 180;
        const timeOffset = (now.getUTCHours() + now.getUTCMinutes() / 60) * 15 - 180; // degrees
        
        // Create terminator line positions
        const positions = [];
        for (let lat = -90; lat <= 90; lat += 2) {
            // Calculate longitude where the sun angle is 0 (terminator)
            const latRad = lat * Math.PI / 180;
            const hourAngle = Math.acos(-Math.tan(latRad) * Math.tan(sunDeclination));
            
            if (!isNaN(hourAngle)) {
                const lon1 = (hourAngle * 180 / Math.PI + timeOffset) % 360;
                const lon2 = (-hourAngle * 180 / Math.PI + timeOffset) % 360;
                
                // Normalize longitudes to -180 to 180
                const normLon1 = lon1 > 180 ? lon1 - 360 : lon1 < -180 ? lon1 + 360 : lon1;
                const normLon2 = lon2 > 180 ? lon2 - 360 : lon2 < -180 ? lon2 + 360 : lon2;
                
                positions.push(new WorldWind.Position(lat, normLon1, 100000));
                positions.push(new WorldWind.Position(lat, normLon2, 100000));
            }
        }
        
        if (positions.length > 2) {
            // Create night side polygon
            const nightPositions = this.generateNightSidePolygon(positions, sunDeclination, timeOffset);
            
            const nightPolygon = new WorldWind.Polygon(nightPositions, null);
            nightPolygon.altitudeMode = WorldWind.RELATIVE_TO_GROUND;
            
            const nightAttributes = new WorldWind.ShapeAttributes(null);
            nightAttributes.interiorColor = new WorldWind.Color(0, 0, 0.2, 0.4); // Dark blue overlay
            nightAttributes.outlineColor = new WorldWind.Color(0.2, 0.2, 0.6, 0.6); // Twilight blue
            nightAttributes.outlineWidth = 2;
            nightAttributes.drawOutline = true;
            nightAttributes.drawInterior = true;
            
            nightPolygon.attributes = nightAttributes;
            this.dayNightLayer.addRenderable(nightPolygon);
        }
    }

    generateNightSidePolygon(terminatorPositions, sunDeclination, timeOffset) {
        // Create a polygon covering the night side of Earth
        const positions = [];
        
        // Add terminator line
        terminatorPositions.forEach(pos => positions.push(pos));
        
        // Add perimeter to close the night polygon
        // This is a simplified approach - in reality, you'd need more complex geometry
        const nightSideLon = (timeOffset + 180) % 360;
        const normNightLon = nightSideLon > 180 ? nightSideLon - 360 : nightSideLon;
        
        // Add corners to create closed polygon
        positions.push(new WorldWind.Position(90, normNightLon, 100000));
        positions.push(new WorldWind.Position(-90, normNightLon, 100000));
        
        return positions;
    }

    startDayNightAnimation() {
        // Update day/night terminator every 5 minutes
        setInterval(() => {
            this.updateDayNightTerminator();
            this.wwd.redraw();
        }, 300000); // 5 minutes
    }

    startDayNightAnimation() {
        // Update day/night terminator every 5 minutes
        setInterval(() => {
            this.updateDayNightTerminator();
            this.wwd.redraw();
        }, 300000); // 5 minutes
    }

